import{_ as t,c as i,o as s,U as l}from"./chunks/framework.NMZE9xmb.js";const e="/assets/monitor.uecBYv6e.png",a="/assets/assets.fnCBJD25.png",b=JSON.parse('{"title":"MONITOR","description":"CTO Plus技术栈","frontmatter":{"home":true,"title":"MONITOR","editLink":true,"description":"CTO Plus技术栈","layout":"home","hero":{"name":"Monitor","text":"运维监控告警中心","tagline":"集成了智能化监控、实时告警和数据分析功能，旨在帮助企业实现全面的运维管控，提升运营效率，降低故障响应时间。","image":{"src":"/logo2.svg","alt":"VitePress"},"actions":[{"theme":"brand","text":"关于我们","link":"/about_me/"},{"theme":"sponsor","text":"技术中心","link":"/index"},{"theme":"brand","text":"产品中心","link":"/index_pro"},{"theme":"sponsor","text":"申请试用","link":"/about_me/trial_use.md"}]}},"headers":[],"relativePath":"products/characteristic/monitor.md","filePath":"products/characteristic/monitor.md","lastUpdated":1726852813000}'),d={name:"products/characteristic/monitor.md"},c=l('<div class="home-plus"><div style=""><div class="title-plus">功能</div><div style="display:flex;justify-content:center;margin-bottom:20px;"><img src="'+e+'" style=""></div><div style="text-align:center;"><div class="card-plus"><div class="sub-title-plus">智能化运维监控</div><div class="content-plus">利用先进的监控技术和算法，自动识别系统的异常行为，实现对关键应用和基础设施的全天候监测，确保业务连续性。</div></div><div class="card-plus"><div class="sub-title-plus">实时告警</div><div class="content-plus">通过灵活的告警策略和个性化配置，通过企业告警媒介（告警邮件、短信、电话、钉钉、企微，以及第三方IM工具）及时推送故障、性能下降等关键事件的告警，帮助运维团队迅速响应，减少潜在影响。</div></div><div class="card-plus"><div class="sub-title-plus">数据驱动的决策支持</div><div class="content-plus">采用数据分析与可视化工具，平台能够提供详细的性能报告和趋势分析，帮助用户做出明智、有效的运维决策。</div></div></div><div style="text-align:center;"><div class="card-plus"><div class="sub-title-plus">多维监测能力</div><div class="content-plus">支持对网络、服务器、数据库和应用等多层级的全面监测，提供一站式运维视图，便于用户快速识别潜在风险。</div></div><div class="card-plus"><div class="sub-title-plus">自定义告警规则</div><div class="content-plus">用户可以根据不同的业务需求设置告警阈值和策略，灵活应对各种运维场景，确保关键事件的及时处理。</div></div><div class="card-plus"><div class="sub-title-plus">智能事件关联分析</div><div class="content-plus">利用大数据分析技术，将相关事件进行智能分析和关联，降低故障排查的时间和复杂度，提升问题解决速度。</div></div></div></div><div style="display:flex;justify-content:center;margin-bottom:20px;"><img src="'+a+'" style=""></div><div class="title-plus">功能特点</div><div class="sub-content-plus"><li>统一监控：在由CMDB提供的基础数据下，提供了机房监控、主机监控（云主机）、业务监控、设备监控（服务器设备、办公设备、网络设备）。</li><li>数据库监控：提供常用的MySQL、PostgreSQL、MongoDB、TiDB数据监控。</li><li>应用监控：提供对Java、Go、Python、C、C++、Node等实现的应用进行监控。</li><li>中间件监控：Nginx、Kafka、RocketMQ、RabbitMQ、ElasticSearch、Redis监控。</li><li>API接口监控：接口调用链、接口异常监控。</li><li>前端监控：提供Web、移动端APP和小程序的监控。</li><li>资源和成本监控：对各方面的资源使用情况，以及资源所耗费的成本进行监控。</li><li>SSL证书监控：SSL证书的有效性和到期监控。</li><li>告警通知：针对监控到的异常信息，进行多媒介告警：邮件、短信、电话、钉钉、企微，以及第三方IM工具。</li><li>系统集成：集成第三方告警监控系统Prometheus、Zabbix。</li></div><div class="title-plus">关联产品</div><div class="sub-content-plus"><li>与<a href="./mwms.html" target="_blank">中间件管理系统（MWMS）</a>联动</li><li>与<a href="./dbms.html" target="_blank">数据库管理系统（DBMS）</a>联动</li><li>与<a href="./aiflow.html" target="_blank">自动化工作流系统（AIFlow）</a>联动</li><li>与<a href="./itsm.html" target="_blank">IT流程管理系统（ITSM）</a>联动</li><li>与<a href="./cicd.html" target="_blank">自动化构建与发布系统（CICD）</a>联动</li></div><div class="title-plus">应用场景</div><div class="sub-content-plus"> 可以应用在以下方面 <li>企业资源治理。</li><li>应用故障诊断。</li><li>业务故障提前预知和及时接收故障消息。</li><li>企业统一的监控告警平台。</li></div><div class="title-plus">联系我们</div><div class="sub-content-plus"> 如需了解更多信息，欢迎与我们的销售团队联系： <li><span style="font-weight:bold;">电话：</span>17558477242</li><li><span style="font-weight:bold;">邮箱：</span>rocket_2014@126.com</li><li><span style="font-weight:bold;">官网(唯一)：</span><a href="http://116.205.137.183/" target="_blank">http://116.205.137.183/</a></li></div></div><link rel="stylesheet" href="./../static/index.css">',2),n=[c];function o(r,v,p,u,m,_){return s(),i("div",null,n)}const f=t(d,[["render",o]]);export{b as __pageData,f as default};
